create database customer_behavior;
use customer_behavior;
select * from customer;

#total revenue generated by male vs female customers
select gender, sum(purchase_amount) as revenue 
from customer
group by gender;

#which customers used a discount but still spent more than avearge purchase amount
select customer_id, purchase_amount 
from customer
where discount_applied = "Yes" and purchase_amount >= (select avg(purchase_amount) from customer);

#which are the top 5 products with the highest average review rating?
select item_purchased, round(avg(review_rating),2) as Average_product_rating 
from customer
group by item_purchased
order by avg(review_rating) desc
limit 5;

#compare the average purchase amounts between standard and express shipping
select shipping_type, round(avg(purchase_amount),2)
from customer
where shipping_type in ("Standard", "Express")
group by shipping_type;

#do subscribed customers spend more? compare avg spend and total revenue between subscribers and non-subscribed customers
select subscription_status, 
count(customer_id) as Total_customers, 
round(avg(purchase_amount),2) as Avg_spend,
round(sum(purchase_amount),2) as Total_revenue
from customer
group by subscription_status
order by Total_revenue, Avg_spend desc;

#which 5 products have the highest percentage of purchases with discounts applied?
#here have to calculate discount rate per product
#finding which product rely heavily on discounts to sell
#grouping the items and finding the percent of times discount was applied to an item out of total no.of times the item was sold 
#if hat was 10 times 50% of times that is 5 times it was sold when on discount  
select item_purchased, 
round(sum(case when discount_applied = 'Yes' then 1 else 0 end)/count(*) * 100,2) as discount_rate
from customer
group by item_purchased
order by discount_rate desc limit 5;

#segment customers into new, returning, and loyal based on their total number of previous purchases 
#and show the count of each segment
with customer_type as (
select customer_id, previous_purchases,
case 
	when previous_purchases = 1 then "New" 
	when previous_purchases between 2 and 10 then "Returning"
	else "Loyal"
	end as customer_segment
from customer
)

select customer_segment, count(*) as "Number of Customers"
from customer_type
group by customer_segment;

#what are the top 3 most purchased products within each category?
with item_count as (
select category,
item_purchased,
count(customer_id) as Total_orders,
row_number() over(partition by category order by count(customer_id) desc) as item_rank
from customer
group by category, item_purchased
)
select item_rank, category, item_purchased, Total_orders
from item_count
where item_rank <= 3;

#are customers who are repeat buyers (more than 5 previous purchases) also likely to subscribe?
select subscription_status, count(customer_id) as Repeat_buyers
from customer
where previous_purchases > 5
group by subscription_status;

#what is the revenue contribution of each group?
select age_group, sum(purchase_amount) as Total_revenue 
from customer 
group by age_group
order by Total_revenue desc;





